const results = [];
for (const item of items) {
  try {
    const content = item.json.choices?.[0]?.message?.content || '{}';
    let memoryData;
    try {
      memoryData = typeof content === 'string' ? JSON.parse(content) : content;
    } catch (parseError) {
      console.error('Erro ao parsear JSON da memória:', parseError);
      memoryData = { conversation_summary: '', new_resolved_issues: [], new_pending_issues: [], key_topics: [], sentiment: 'neutral' };
    }
    const normalized = {
      conversation_summary: memoryData.conversation_summary || '',
      new_resolved_issues: Array.isArray(memoryData.new_resolved_issues) ? memoryData.new_resolved_issues : [],
      new_pending_issues: Array.isArray(memoryData.new_pending_issues) ? memoryData.new_pending_issues : [],
      key_topics: Array.isArray(memoryData.key_topics) ? memoryData.key_topics : [],
      sentiment: memoryData.sentiment || 'neutral'
    };
    results.push({ json: normalized });
  } catch (error) {
    console.error('Erro ao processar memória:', error);
    results.push({ json: { conversation_summary: '', new_resolved_issues: [], new_pending_issues: [], key_topics: [], sentiment: 'neutral' } });
  }
}
return results;

function sanitizeString(str, maxLength = 500) {
  if (typeof str !== 'string') return '';
  return str.trim().slice(0, maxLength);
}
function sanitizePhoneNumber(phone) {
  if (!phone) return '';
  const cleaned = String(phone).replace(/[^0-9]/g, '');
  return cleaned.slice(0, 20);
}
function extractContactInfo(value) {
  const contacts = value.contacts || [];
  const contact = contacts[0] || {};
  const name = sanitizeString(contact.profile?.name || contact.profile?.Name || contact.name || 'Contato Meta');
  const waId = sanitizePhoneNumber(contact.wa_id);
  return { name, waId };
}
function extractMessageText(message) {
  const messageType = message.type || 'text';
  let text = '';
  try {
    switch (messageType) {
      case 'text': text = message.text?.body || ''; break;
      case 'interactive':
        const interactive = message.interactive || {};
        if (interactive.type === 'list_reply') text = interactive.list_reply?.title || interactive.list_reply?.description || '';
        else if (interactive.type === 'button_reply') text = interactive.button_reply?.title || interactive.button_reply?.id || '';
        break;
      case 'button': text = message.button?.text || ''; break;
      case 'reaction': text = message.reaction?.emoji || ''; break;
      case 'image': case 'video': case 'audio': case 'document':
        text = message[messageType]?.caption || message[messageType]?.filename || '';
        break;
      default: text = '';
    }
  } catch (error) { text = ''; }
  return text || `[${messageType} sem texto]`;
}
function createTimestamp(messageTimestamp) {
  try {
    const timestampSeconds = Number(messageTimestamp || Date.now() / 1000);
    if (Number.isNaN(timestampSeconds)) return new Date().toISOString();
    return new Date(timestampSeconds * 1000).toISOString();
  } catch (error) { return new Date().toISOString(); }
}
function validateEvent(item) {
  if (!item || !item.json) throw new Error('Item inválido: estrutura JSON ausente');
  const event = item.json.event || {};
  const messages = event.messages;
  if (!Array.isArray(messages) || messages.length === 0) {
    console.warn('Evento sem mensagens válidas');
    return false;
  }
  return true;
}
const results = [];
const errors = [];
for (let i = 0; i < items.length; i++) {
  try {
    if (!validateEvent(items[i])) continue;
    const base = items[i].json || {};
    const config = base.config || {};
    const value = base.event || {};
    const messages = value.messages || [];
    const metaBase = { ...(base.meta || {}) };
    metaBase.environment = config.meta_env || metaBase.environment || 'sandbox';
    metaBase.provider = 'meta';
    metaBase.version = 'v5.009';
    const { name: contactName, waId } = extractContactInfo(value);
    for (const message of messages) {
      try {
        const senderWaId = sanitizePhoneNumber(message.from || waId);
        if (!senderWaId) continue;
        const isoTimestamp = createTimestamp(message.timestamp);
        const messageType = sanitizeString(message.type || 'text', 50);
        const text = sanitizeString(extractMessageText(message), 4000);
        const meta = { ...metaBase };
        meta.client_mask = senderWaId.slice(-4);
        meta.route = 'meta_inbound';
        meta.processed_at = new Date().toISOString();
        const normalized = {
          sender: senderWaId,
          type: messageType,
          text: text,
          timestamp: isoTimestamp,
          message_id: sanitizeString(message.id || '', 100),
          context_id: sanitizeString(message.context?.id || '', 100),
          raw: message,
          config,
          meta,
          client_id: senderWaId,
        };
        results.push({
          json: {
            client_id: senderWaId,
            client_name: contactName,
            message_content: text,
            message_type: messageType,
            message_id: normalized.message_id,
            timestamp: isoTimestamp,
            direction: 'inbound',
            channel: 'whatsapp:meta',
            normalized,
            meta,
            config,
            event: value,
          },
        });
      } catch (messageError) {
        errors.push({ stage: 'message_processing', message_id: message.id, error: messageError.message });
      }
    }
  } catch (itemError) {
    errors.push({ stage: 'item_processing', item_index: i, error: itemError.message });
  }
}
if (results.length === 0) throw new Error('Nenhuma mensagem válida foi processada');
return results;

const results = [];
for (const item of items) {
  // Preferir raw body vindo em binário quando Webhook está com rawBody/binaryData
  const binarySources = Object.values(item.binary || {});
  let rawBody = '';
  if (binarySources.length && binarySources[0]?.data) {
    rawBody = Buffer.from(binarySources[0].data, 'base64').toString('utf8');
  } else if (item.binary?.rawBody?.data) {
    rawBody = Buffer.from(item.binary.rawBody.data, 'base64').toString('utf8');
  } else if (typeof item.json.body === 'string') {
    rawBody = item.json.body;
  } else if (item.json.body !== undefined) {
    try {
      rawBody = JSON.stringify(item.json.body);
    } catch (error) {
      rawBody = '';
    }
  }

  let parsedBody = null;
  if (rawBody) {
    try {
      parsedBody = JSON.parse(rawBody);
    } catch (error) {
      parsedBody = null;
    }
  } else if (item.json.body && typeof item.json.body === 'object') {
    parsedBody = item.json.body;
  }

  const entry = parsedBody?.entry?.[0] || {};
  const change = entry.changes?.[0] || {};
  const value = change.value || null;
  const meta = {
    start_at: Date.now(),
    provider: 'meta',
    channel: 'whatsapp',
    environment: item.json.config?.meta_env || item.json.meta?.environment || null,
    phone_number_id: value?.metadata?.phone_number_id || null,
    display_phone_number: value?.metadata?.display_phone_number || null,
  };

  results.push({
    json: {
      meta,
      headers: item.json.headers || {},
      query: item.json.query || {},
      raw_body: rawBody,
      entry,
      event: value,
      method: item.json.httpMethod || item.json.method || 'POST',
      config: item.json.config || {},
    },
  });
}
return results;

const items = $input.all();
function buildResult(item, extra) {
  return {
    json: {
      ...item.json,
      signature: {
        valid: extra.valid,
        reason: extra.reason,
        header: extra.header ?? null,
        expected: extra.expected ?? null,
      },
    },
  };
}
return items.map((item) => {
  const json = item.json || {};
  const config = json.config || {};
  const rawBody = json.raw_body;
  const headerSignature = json.headers?.['x-hub-signature-256'];
  const hmacHex = json.expected_signature_hmac;
  if (!config.app_secret) {
    return buildResult(item, {
      valid: false,
      reason: 'META_APP_SECRET não configurado',
      header: headerSignature || 'missing',
      expected: null,
    });
  }
  if (!rawBody) {
    return buildResult(item, {
      valid: false,
      reason: 'raw_body ausente',
      header: headerSignature || 'missing',
      expected: null,
    });
  }
  if (!headerSignature) {
    return buildResult(item, {
      valid: false,
      reason: 'Header X-Hub-Signature-256 ausente',
      header: 'missing',
      expected: null,
    });
  }
  if (!hmacHex) {
    return buildResult(item, {
      valid: false,
      reason: 'Assinatura esperada (HMAC) não calculada',
      header: headerSignature,
      expected: null,
    });
  }
  const expectedSignature = 'sha256=' + String(hmacHex).trim().toLowerCase();
  const candidate = String(headerSignature).trim().toLowerCase();
  const valid =
    candidate.length === expectedSignature.length &&
    candidate === expectedSignature;
  return buildResult(item, {
    valid,
    reason: valid ? 'Assinatura válida' : 'Assinatura inválida',
    header: headerSignature,
    expected: expectedSignature,
  });
});

return items.map(item => {
  const meta = item.json.meta || {};
  meta.http_status = 401;
  meta.route = 'meta_signature_invalid';
  meta.success = false;
  meta.response = { status: 'error', reason: 'invalid_signature' };
  item.json.meta = meta;
  return { json: item.json };
});

const results = [];
for (const item of items) {
  const statuses = item.json.event?.statuses || [];
  if (!statuses.length) {
    const meta = item.json.meta || {};
    meta.route = 'meta_status_empty';
    meta.http_status = 200;
    meta.success = true;
    meta.response = { status: 'ignored' };
    results.push({ json: { meta } });
    continue;
  }
  for (const status of statuses) {
    const meta = { ...(item.json.meta || {}) };
    meta.route = 'meta_status';
    meta.http_status = 200;
    meta.success = status.status !== 'failed';
    meta.response = {
      status: status.status,
      message_id: status.id || null,
      conversation_id: status.conversation?.id || null,
      timestamp: status.timestamp || null,
    };
    if (status.errors?.length) {
      meta.error = status.errors[0].code || 'status_error';
      meta.response.error = status.errors[0];
      meta.success = false;
    } else {
      delete meta.error;
    }
    results.push({ json: { meta, status } });
  }
}
return results;

return items.map(item => {
  const meta = item.json.meta || {};
  meta.route = 'meta_unknown_event';
  meta.http_status = 200;
  meta.success = true;
  meta.response = { status: 'ignored', message: 'event-type-not-handled' };
  item.json.meta = meta;
  return { json: item.json };
});

const inputMain = this.getInputData(0);
const inputContext = this.getInputData(1);
const outputs = [];
for (let index = 0; index < inputMain.length; index += 1) {
  const gptItem = inputMain[index];
  const contextItem = inputContext?.[index] || { json: {} };
  const normalized = contextItem.json?.normalized || contextItem.json || {};
  const config = normalized.config || {};
  const meta = { ...(normalized.meta || {}) };
  const gptChoice = gptItem.json?.choices?.[0]?.message?.content || '';
  const cleanedText = (gptChoice || '').trim();
  const templateHint = normalized.meta?.outbound_template || gptItem.json?.template || null;
  const responseType = templateHint?.name || templateHint?.id ? 'template' : 'text';
  const requestBody = {
    messaging_product: 'whatsapp',
    to: normalized.sender || normalized.client_id || '',
  };
  if (normalized.message_id) {
    requestBody.context = { message_id: normalized.message_id };
  }
  if (responseType === 'template') {
    const templateName = templateHint.name || templateHint.id;
    const templateLanguage = templateHint.language || config.template_language || 'pt_BR';
    const templateComponents = templateHint.components || (templateHint.params
      ? [
          {
            type: 'body',
            parameters: templateHint.params.map(param => ({ type: 'text', text: param })),
          },
        ]
      : undefined);
    requestBody.type = 'template';
    requestBody.template = {
      name: templateName,
      language: { code: templateLanguage },
    };
    if (templateComponents) {
      requestBody.template.components = templateComponents;
    }
  } else {
    requestBody.type = 'text';
    requestBody.text = {
      preview_url: false,
      body: cleanedText || '...'
    };
  }
  const graphVersion = config.graph_version || 'v20.0';
  const phoneNumberId = config.phone_number_id || '';
  const baseUrl = config.base_url || `https://graph.facebook.com/${graphVersion}`;
  const outbound = {
    type: responseType,
    environment: config.meta_env || meta.environment || 'sandbox',
    request: {
      method: 'POST',
      url: `${baseUrl}/${phoneNumberId}/messages`,
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${config.access_token || ''}`,
      },
      body: requestBody,
    },
  };
  meta.route = responseType === 'template' ? 'meta_prepare_template' : 'meta_prepare_text';
  meta.response = { status: 'queued' };
  outputs.push({ json: { outbound, meta, normalized, config } });
}
return outputs;

const wait = (ms) => new Promise(resolve => setTimeout(resolve, ms));
const maxRetries = 2;
const results = [];
for (const item of items) {
  const outbound = item.json.outbound || {};
  const request = outbound.request || {};
  const meta = { ...(item.json.meta || {}) };
  const normalized = item.json.normalized || {};
  if (!request.url || !request.method || !request.headers?.Authorization) {
    meta.http_status = 500;
    meta.success = false;
    meta.response = { status: 'error', message: 'missing_meta_credentials' };
    meta.error = 'missing_meta_credentials';
    results.push({ json: { meta, outbound, normalized, delivery: { success: false, statusCode: 0, retries: 0 } } });
    continue;
  }
  let retries = 0;
  let success = false;
  let statusCode = 0;
  let body = null;
  let errorCategory = null;
  while (true) {
    let shouldRetry = false;
    try {
      const response = await this.helpers.httpRequest({
        method: request.method,
        url: request.url,
        headers: request.headers,
        body: request.body,
        json: true,
        resolveWithFullResponse: true,
      });
      statusCode = response.statusCode;
      body = response.body;
      if (statusCode >= 500) {
        errorCategory = 'server';
        shouldRetry = retries < maxRetries;
      } else if (statusCode >= 400) {
        errorCategory = 'client';
        shouldRetry = false;
      } else {
        success = true;
      }
    } catch (error) {
      statusCode = error.statusCode || error.response?.statusCode || 500;
      body = error.response?.body || error.message;
      errorCategory = statusCode >= 500 ? 'server' : 'client';
      shouldRetry = errorCategory === 'server' && retries < maxRetries;
    }
    if (success || !shouldRetry) {
      break;
    }
    retries += 1;
    const backoffMs = Math.min(10000, Math.pow(2, retries) * 1000);
    await wait(backoffMs);
  }
  const httpStatus = success ? statusCode || 200 : statusCode || 502;
  meta.http_status = httpStatus;
  meta.success = success;
  meta.route = outbound.type === 'template' ? 'meta_send_template' : 'meta_send_text';
  meta.response = success
    ? { status: 'sent', provider_status: statusCode, message_id: body?.messages?.[0]?.id || null }
    : { status: 'error', provider_status: statusCode, category: errorCategory, details: body?.error || body };
  if (!success) {
    meta.error = errorCategory === 'server' ? 'meta_server_error' : 'meta_client_error';
  } else {
    delete meta.error;
  }
  results.push({ json: { meta, outbound, normalized, delivery: { success, statusCode, body, retries, errorCategory } } });
}
return results;

return items.map(item => {
  const meta = item.json.meta || {};
  const startAt = meta.start_at || Date.now();
  meta.latency_ms = Date.now() - startAt;
  meta.provider = meta.provider || 'meta';
  if (!meta.http_status) {
    meta.http_status = 200;
  }
  if (!meta.response) {
    meta.response = { status: meta.success === false ? 'error' : 'ok' };
  }
  item.json = { meta };
  return item;
});

return items.map(item => {
  const meta = item.json.meta || {};
  const log = {
    timestamp: new Date().toISOString(),
    route: meta.route || 'unknown',
    environment: meta.environment || 'sandbox',
    provider: meta.provider || 'meta',
    status: meta.http_status || 200,
    success: meta.success !== false,
    latency_ms: meta.latency_ms || 0,
  };
  if (meta.client_mask) {
    log.client_mask = meta.client_mask;
  }
  if (meta.error) {
    log.error = meta.error;
  }
  if (meta.response?.provider_status) {
    log.provider_status = meta.response.provider_status;
  }
  if (meta.response?.message_id) {
    log.message_id = meta.response.message_id;
  }
  console.log('[meta-metrics]', JSON.stringify(log));
  return { json: { meta } };
});

