// Handshake GET Meta: valida verify_token e retorna hub.challenge
const query = $input.item.json.query || $input.item.json.event?.query || {};
const config = $input.item.json.config || {};
const verifyToken = config.verify_token || '';
const incomingToken = query['hub.verify_token'];
const challenge = query['hub.challenge'];
const ok = !!challenge && incomingToken === verifyToken;
const meta = {
  route: ok ? 'meta_verify_ok' : 'meta_verify_fail',
  http_status: ok ? 200 : 403,
  success: ok,
};
return [{
  json: {
    challenge: ok ? challenge : 'invalid_verify_token',
    meta,
  }
}];

