const memoriaItems = $input.all(0);
const mensagensItems = $input.all(1);
const historicoItems = $input.all(2);
const kbItems = $input.all(3);
const byClient = new Map();

function ensure(clientId) {
  if (!byClient.has(clientId)) {
    byClient.set(clientId, {
      client_id: clientId,
      client_name: 'Cliente',
      message_content: '',
      memoria_cliente: null,
      mensagens_recentes: [],
      historico_similar: [],
      knowledge_base: [],
      normalized: {},
      config: {},
      meta: {},
    });
  }
  return byClient.get(clientId);
}

for (const item of memoriaItems) {
  const clientId = item.json?.client_id;
  if (!clientId) continue;
  const target = ensure(clientId);
  target.memoria_cliente = item.json;
  target.client_name = item.json?.client_name || target.client_name;
  target.message_content = item.json?.message_content || target.message_content;
  target.normalized = item.json?.normalized || target.normalized;
  target.config = item.json?.config || target.config;
  target.meta = item.json?.meta || target.meta;
}

for (const item of mensagensItems) {
  const clientId = item.json?.client_id;
  if (!clientId) continue;
  const target = ensure(clientId);
  target.mensagens_recentes.push(item.json);
  if (!target.message_content) {
    target.message_content = item.json?.content || '';
  }
}

for (const item of historicoItems) {
  const clientId = item.json?.client_id || item.json?.matches?.[0]?.metadata?.client_id;
  if (!clientId) continue;
  const target = ensure(clientId);
  target.historico_similar = item.json?.matches || target.historico_similar;
}

for (const item of kbItems) {
  const clientId = item.json?.client_id || item.json?.matches?.[0]?.metadata?.client_id;
  if (!clientId) continue;
  const target = ensure(clientId);
  target.knowledge_base = item.json?.matches || target.knowledge_base;
}

return Array.from(byClient.values()).map((json) => ({ json }));
